import styled from "styled-components";

export const OrderSummeryContainer = styled.div`
  width: 750px;
  display: flex;
  flex-direction: column;
  margin: auto;
  align-items: center;
`;

export const OrderDetailsContainer = styled.div`
  display: flex;
  flex-direction: row;

  svg {
    width: 150px;
    height: 150px;
  }
`;
