import styled, { css } from "styled-components";

const subColor = "grey";
const mainColor = "black";

const shrinkLabelStyles = css`
  top: -14px;
  font-size: 15px;
  color: ${mainColor};
`;

export const GroupContainer = styled.div`
  position: relative;
  margin: 45px 0;

  input[type="password"] {
    letter-spacing: 0.3em;
  }
`;

const getInputStyles = props => {
  if (props.isScrollable) {
    return scrollableStyle;
  }
  return basicInputStyle;
};

const scrollableStyle = css`
  border-bottom: 1px solid ${subColor};

  &:focus {
    outline: none;
    min-height: 250px;
  }
`;

const basicInputStyle = css`
  border-radius: 0;
  border-bottom: 1px solid ${subColor};

  &:focus {
    outline: none;
  }

  &:focus ~ label {
    ${shrinkLabelStyles}
  }
`;

const basicStyles = css`
  background: none;
  background-color: white;
  color: ${subColor};
  font-size: 20px;
  padding: 10px 10px 10px 5px;
  display: block;
  width: 100%;
  margin: 25px 0;
  border: none;
  min-height: 40px;
  transition: 500ms ease all;
`;

const getStyles = props => {
  props.isScrollable
    ? styled.input`
        background-color: black;
      `
    : styled.textarea`
        background-color: red;
      `;
};

export const FormInputContainer = getStyles();

export const FormInputLabel = styled.label`
  color: ${subColor};
  font-size: 18px;
  font-weight: normal;
  position: absolute;
  pointer-events: none;
  left: 5px;
  top: 10px;
  transition: 300ms ease all;

  &.shrink {
    ${shrinkLabelStyles}
  }
`;
