import React from "react";
import "./OrderSummery.scss";

const OrderSummery = ({ location }) => {
  const { card } = location.state.token;
  console.log(card);
  return (
    <div className="order-summery">
      <h1 className="title">Your order has been placed!</h1>
      <h3 className="subtitle">
        Be pacient {card.name.toUpperCase()}, it'll worth it!
      </h3>

      <span>Shipping Address: </span>
      <span>{card.address_country}, {card.address_city}, {card.address_line1}, {card.address_zip}</span>
    </div>
  );
};

export default OrderSummery;
