import React from "react";
import { connect } from "react-redux";

import { addItem } from "../../redux/cart/cart.actions";

import { ReactComponent as WishListIconColored } from "../../assets/wishlist.svg";
import { ReactComponent as WishListIcon } from "../../assets/wishlist-colored.svg";

import {
  CollectionItemContainer,
  CollectionFooterContainer,
  AddButton,
  BackgroundImage,
  NameContainer,
  PriceContainer,
  WishlistIconContainer
} from "./collection-item.styles";

class CollectionItem extends React.Component {
  state = { inWishlist: false };

  render() {
    const { item, addItem } = this.props;
    const { name, price, imageUrl } = item;
    return (
      <CollectionItemContainer>
        <BackgroundImage className="image" imageUrl={imageUrl} />
        <CollectionFooterContainer>
          <NameContainer>{name}</NameContainer>
          <PriceContainer>{price}&#8362;</PriceContainer>
          <WishlistIconContainer>
            {this.state.inWishlist ? (
              <WishListIcon  />
            ) : (
              <WishListIconColored />
            )}
          </WishlistIconContainer>
        </CollectionFooterContainer>
        <AddButton onClick={() => addItem(item)} inverted>
          Add to cart
        </AddButton>
      </CollectionItemContainer>
    );
  }
}

const mapDispatchToProps = dispatch => ({
  addItem: item => dispatch(addItem(item))
});

export default connect(null, mapDispatchToProps)(CollectionItem);
