import SHOP_DATA_NEW from "../../shopData";

const INITIAL_STATE = {
  collections: SHOP_DATA_NEW
};

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.payload) {
    default:
      return state;
  }
};

export default shopReducer;
